git_hook_implementation.content:
#!/bin/bash # Pre-commit hook for rule validation
set -e
echo "Running rule validation checks..."
# Check for rule file changes RULE_FILES=$(git diff --cached --name-only | grep -E '\.(yaml|yml)$' | grep -E '^(rules/|\.roo/)' || true)
if [ -z "$RULE_FILES" ]; then echo "No rule files changed, skipping validation." exit 0 fi
# Validate rule syntax echo "Validating rule syntax..." for file in $RULE_FILES; do if [ -f "$file" ]; then yamllint "$file" || { echo "YAML syntax error in $file" exit 1 } echo "âœ“ $file syntax OK" fi done
# Validate rule schemas echo "Validating rule schemas..." python scripts/validate_rule_schemas.py $RULE_FILES || { echo "Rule schema validation failed" exit 1 }
# Check for dependency conflicts echo "Checking rule dependencies..." python scripts/check_rule_dependencies.py $RULE_FILES || { echo "Rule dependency check failed" exit 1 }
# Generate impact assessment echo "Generating impact assessment..." python scripts/generate_impact_assessment.py $RULE_FILES > .git/rule_impact_assessment.md
echo "All rule validation checks passed!" echo "Impact assessment saved to .git/rule_impact_assessment.md"

